import React, { useState, useEffect } from "react";
import "./Main.css";
import { Link, useNavigate } from "react-router-dom";

import CategoriesData from "../datas/CategoriesData";
import CategoriesWomenData from "../datas/CategoriesWomenData";
import CategoriesLimelight from "../datas/CategoriesLimelight";
import FeedbackData from "../datas/FeedbackData";
import SingleProduct from "../datas/Single-product";
import { BiCategory } from "react-icons/bi";

export const Main = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [totalVisible, setTotalVisible] = useState(4);
  const navigate = useNavigate();

  const updateVisibleItems = () => {
    let newTotal = 4;
    if (window.innerWidth <= 320) newTotal = 1;
    else if (window.innerWidth <= 480) newTotal = 2;
    else if (window.innerWidth <= 640) newTotal = 2;
    else if (window.innerWidth <= 768) newTotal = 3;
    else if (window.innerWidth <= 980) newTotal = 4;
    else newTotal = 4;

    setTotalVisible(newTotal);
  };

  useEffect(() => {
    updateVisibleItems();
    window.addEventListener("resize", updateVisibleItems);
    return () => window.removeEventListener("resize", updateVisibleItems);
  }, []);

  const newArrivalsData = [
    { id: 1, name: "Knitted Joggers", description: "Explore Now!", image: "assets/img-1.png" },
    { id: 2, name: "Full Sleeve", description: "Explore Now!", image: "assets/img-3.jpg" },
    { id: 3, name: "Active T-Shirts", description: "Explore Now!", image: "assets/img-4.jpg" },
    { id: 4, name: "Urban Shirts", description: "Explore Now!", image: "assets/img-2.jpg" },
    { id: 5, name: "Denim Jacket", description: "Explore Now!", image: "assets/img-1.png" },
    { id: 6, name: "Casual Hoodie", description: "Explore Now!", image: "assets/img-3.jpg" },
    { id: 7, name: "Summer Shorts", description: "Explore Now!", image: "assets/img-4.jpg" },
    { id: 8, name: "Sneakers", description: "Explore Now!", image: "assets/img-2.jpg" },
  ];

  const maxSlides = Math.max(Math.ceil(newArrivalsData.length / totalVisible) - 1, 0);

  const handleClickLeft = () => {
    setCurrentSlide((prevSlide) => Math.max(prevSlide - 1, 0));
  };

  const handleClickRight = () => {
    setCurrentSlide((prevSlide) => Math.min(prevSlide + 1, maxSlides));
  };

  return (
    <>
      <div className="Categories-women wrapper">
        <div className="top">
          <h1>Categories For Women</h1>
        </div>
        <div className="bottom">
          <div className="container">
            {CategoriesWomenData.map((item) => (
              <Link to={`/product/${item.id}`} key={item.id} className="category-card">
                <img src={item.image} alt={item.name} className="primary-img" />
                <div className="category-info">
                  <h2>{item.name}</h2>
                  <p>{item.description}</p>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>

      <div className="new-arrivals wrapper">
        <div className="top">
          <h1>New Arrivals</h1>
        </div>
        <div className="bottom">
          <button className="slide-btn left" onClick={handleClickLeft} disabled={currentSlide === 0}>
            &#10094;
          </button>
          <div className="container slider-wrapper" style={{ transform: `translateX(-${currentSlide * 100}%)` }}>
            {newArrivalsData.map((item) => (
              <Link to={`/product/${item.id}`} key={item.id} className="category-card" style={{ flex: `0 0 ${100 / totalVisible}%` }}>
                <img src={item.image} alt={item.name} className="primary-img" />
                <div className="category-info">
                  <h2>{item.name}</h2>
                  <p>{item.description}</p>
                </div>
              </Link>
            ))}
          </div>
          <button className="slide-btn right" onClick={handleClickRight} disabled={currentSlide >= maxSlides}>
            &#10095;
          </button>
        </div>
      </div>

      {/* Shop Now Section */}
      <div className="shop-now wrapper">
        <div className="container">
          <div className="left">
            <h2>WE MADE YOUR EVERYDAY <br /> FASHION BETTER!</h2>
            <p>
              In our journey to improve everyday fashion, <br />
              euphoria presents EVERYDAY wear range - <br />
              Comfortable & Affordable fashion 24/7
            </p>
            <button className="shop-btn">Shop Now</button>
          </div>
          <div className="right">
            <img src="assets/friends.png" alt="friends" />
          </div>
        </div>
      </div>


      {/* Categories for Men Section */}
      <div className="Categories-men wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" />
          <h1>Categories For Men</h1>
        </div>
        <div className="bottom">
          <div className="container">
            {CategoriesData.map((item) => (
              <Link to={`/product/${item.id}`} key={item.id} className="category-card">
                <img src={item.image} alt={item.name} className="primary-img" />
                <div className="category-info">
                  <div className="info">
                    <h2>{item.name}</h2>
                    <p>{item.description}</p>
                  </div>
                  <div className="rightshop-btn">
                    <img src="assets/arrow-right.svg" alt="RightArrow" />
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>

      {/* Categories for Women Section */}
