import { useState } from "react";

const product = {
  id: 1,
  name: "Raven Hoodie With Black colored Design",
  price: 63.00,
  rating: 3.5,
  reviews: 120,
  sizes: ["XS", "S", "M", "L", "XL"],
  colors: ["#000000", "#D4A373", "#F5F5DC", "#8B0000"], // Black, Beige, Red, Dark Red
  images: [
    "assets/images/raven-hoodie-1.jpg",
    "assets/images/raven-hoodie-2.jpg",
    "assets/images/raven-hoodie-3.jpg",
    "assets/images/raven-hoodie-4.jpg"
  ],
  description: 
    "100% Bio-washed Cotton â€“ makes the fabric extra soft & silky. Flexible ribbed crew neck. Precisely stitched with no pilling & no fading. Provide all-time comfort. Anytime, anywhere. Infinite range of matte-finish HD prints.",
  features: [
    { icon: "secure_payment", text: "Secure payment" },
    { icon: "size_fit", text: "Size & Fit" },
    { icon: "free_shipping", text: "Free shipping" },
    { icon: "returns", text: "Free Shipping & Returns" }
  ],
  specifications: {
    fabric: "Bio-washed Cotton",
    pattern: "Printed",
    fit: "Regular-fit",
    neck: "Round Neck",
    sleeve: "Half-sleeves",
    style: "Casual Wear"
  }
};

const SingleProduct = () => {
  const [cart, setCart] = useState([]);

  const addToCart = () => {
    setCart([...cart, product]);
    alert("Product added to cart!");
  };

  return (
    <div className="product-container">
      <h2>{product.name}</h2>
      <p>${product.price.toFixed(2)}</p>
      <button onClick={addToCart} className="add-to-cart-btn">ðŸ›’ Add to cart</button>
    </div>
  );
};

export { product, SingleProduct };
export default product;
