import React from "react";
import "./Main.css";
import { Link } from "react-router-dom";


export const Main = () => {
  const handleClickLeft = () => {
    console.log("Left button clicked");
  };

  const handleClickRight = () => {
    console.log("Right button clicked");
  };

  return (
    <>
      <div className="spotlight">
        <div className="container">
          <div className="left">
            <h1>T-Shirt / Tops</h1>
            <h2>Summer <br /> Value Pack</h2>
            <p>cool / colorful / comfy</p>
            <button className="btn">Shop Now</button>
            <img src="assets/carousel-indicators.png" alt="carousel"/>
          </div>
        </div>

        {}
        <button className="slide-btn left-btn" onClick={handleClickLeft}>&lt;</button>
        <button className="slide-btn right-btn" onClick={handleClickRight}>&gt;</button>
      </div>

      <div className="explore wrapper">
        <div className="container">
          <ul>
            <li>
              <h1>Low Price</h1>
              <h2>High Coziness</h2>
              <p>UPTO 50% OFF</p>
              <a href="#">Explore Items</a>
            </li>

            <li className="Beyoung">
              <h1>Beyoung Presents</h1>
              <h2>Breezy Summer <br></br>Style</h2>
              <p>UPTO 50% OFF</p>
              <a href="#">Explore Items</a>
            </li>
          </ul>

        </div>
      </div>


      <div className="new-arrival"></div>


    </>

  );


};
