import React from "react";
import { useParams } from "react-router-dom";
import SingleProduct from "./datas/Single-product";


const SinglePage = () => {
    const { productId } = useParams(); // Get product ID from URL params

    const product = SingleProduct.find((item) => item.id === parseInt(productId));

    if (!product) {
      return <h2>Product Not Found</h2>; // Handle case when product is undefined
    }

  return (
    <div className="single-product-container">
      <img src={product.image} alt={product.name} className="product-image" />
      <div className="product-details">
        <h1>{product.name}</h1>
        <p>{product.description}</p>
        <h3>Price: ${product.price}</h3>
        <button className="buy-btn">Buy Now</button>
      </div>
    </div>
  );
};

export default SinglePage;
