import React, { useState } from "react";
import "./Main.css";
import { Link } from "react-router-dom";

export const Main = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Array of image paths and corresponding item names for the "New Arrival" section
  const items = [
    { img: "assets/photographer-white-background.png", text: "Knitted Joggers" },
    { img: "/assets/img-3.jpg", text: "Full Sleeve" },
    { img: "/assets/img-4.jpg", text: "Active T-Shirts" },
    { img: "/assets/img-2.jpg", text: "Urban Shirts" },
    { img: "assets/photographer-white-background.png", text: "Knitted Joggers" },
    { img: "/assets/img-3.jpg", text: "Full Sleeve" },
    { img: "/assets/img-4.jpg", text: "Active T-Shirts" },
    { img: "/assets/img-2.jpg", text: "Urban Shirts" },
  ];

  // Handle the left button click
  const handleClickLeft = () => {
    setCurrentSlide((prevSlide) => (prevSlide === 0 ? items.length - 4 : prevSlide - 4));
  };

  // Handle the right button click
  const handleClickRight = () => {
    setCurrentSlide((prevSlide) => (prevSlide === items.length - 4 ? 0 : prevSlide + 4));
  };

  return (
    <>
      <div className="spotlight">
        <div className="container">
          <div className="left">
            <h1>T-Shirt / Tops</h1>
            <h2>Summer <br /> Value Pack</h2>
            <p>cool / colorful / comfy</p>
            <button className="btn">Shop Now</button>
            <img src={`assets/carousel-${currentSlide + 1}.png`} alt="carousel" />
          </div>
        </div>

        <button className="slide-btn left-btn" onClick={handleClickLeft}>
          &lt;
        </button>
        <button className="slide-btn right-btn" onClick={handleClickRight}>
          &gt;
        </button>
      </div>

      <div className="explore wrapper">
        <div className="container">
          <ul>
            <li>
              <h1>Low Price</h1>
              <h2>High Coziness</h2>
              <p>UPTO 50% OFF</p>
              <Link to="#">Explore Items</Link>
            </li>

            <li className="Beyoung">
              <h1>Beyoung Presents</h1>
              <h2>Breezy Summer <br /> Style</h2>
              <p>UPTO 50% OFF</p>
              <Link to="#">Explore Items</Link>
            </li>
          </ul>
        </div>
      </div>

      <div className="new-arrival wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" />
          <h1>New Arrival</h1>
        </div>
        <div className="bottom">
          <div className="container">
            <div
              className="new-arrival-list"
              style={{
                transform: `translateX(-${currentSlide * 350}px)`,
                transition: "transform 0.5s ease-in-out",
              }}
            >
              {items.map((item, index) => (
                <div className="new-arrival-item" key={index}>
                  <img src={item.img} alt={item.text} />
                  <p>{item.text}</p>
                </div>
              ))}
            </div>
          </div>
        </div>

        <button className="slide-btn left-btn" onClick={handleClickLeft}>
          &lt;
        </button>
        <button className="slide-btn right-btn" onClick={handleClickRight}>
          &gt;
        </button>
      </div>
    </>
  );
};
