import React, { useState, useEffect } from "react";
import "./Main.css";
import { Link, useNavigate } from "react-router-dom";
import CategoriesData from "../../data/categories.json";  
import CategoriesWomenData from "../../data/categories-women.json";  
import CategoriesLimelight from "../../data/categories-limelight.json";  
import FeedbackData from "../../data/feedback.json";


export const Main = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const [totalVisible, setTotalVisible] = useState(4); // Default to 4 images per slide
  const navigate = useNavigate();

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const imageData = [
    { src: "assets/img-1.png", alt: "Knitted Joggers", label: "Knitted Joggers" },
    { src: "assets/img-3.jpg", alt: "Full Sleeve", label: "Full Sleeve" },
    { src: "assets/img-4.jpg", alt: "Active T-Shirts", label: "Active T-Shirts" },
    { src: "assets/img-2.jpg", alt: "Urban Shirts", label: "Urban Shirts" },
    { src: "assets/img-1.png", alt: "Denim Jacket", label: "Denim Jacket" },
    { src: "assets/img-3.jpg", alt: "Casual Hoodie", label: "Casual Hoodie" },
    { src: "assets/img-4.jpg", alt: "Summer Shorts", label: "Summer Shorts" },
    { src: "assets/img-2.jpg", alt: "Sneakers", label: "Sneakers" },
  ];

  // Dynamic update of images per slide based on screen size
  useEffect(() => {
    const updateVisibleImages = () => {
      if (window.innerWidth <= 640) {
        setTotalVisible(2); // Show 2 images per slide after 640px
      } else if (window.innerWidth <= 768) {
        setTotalVisible(3); // Show 3 images per slide after 768px
      } else {
        setTotalVisible(4); // Default to 4 images per slide
      }
    };

    updateVisibleImages();
    window.addEventListener("resize", updateVisibleImages);

    return () => {
      window.removeEventListener("resize", updateVisibleImages);
    };
  }, []);

  const maxSlides = imageData.length - totalVisible;

  const handleClickLeft = () => {
    setCurrentSlide((prevSlide) => Math.max(prevSlide - 1, 0));
  };

  const handleClickRight = () => {
    setCurrentSlide((prevSlide) => Math.min(prevSlide + 1, maxSlides));
  };

  return (
    <>
       {/* Spotlight Section */}
       <div className="spotlight">
        <div className="container">
          <a href="#"><img src="assets/left-arrow-bold (copy).svg" className="left-arrow"></img></a>
          <div className="left">
            <h1>T-Shirt / Tops</h1>
            <h2>Summer <br /> Value Pack</h2>
            <p>cool / colorful / comfy</p>
            <button className="btn">Shop Now</button>
            <img src="assets/carousel-indicators.png" alt="carousel" />
          </div>
          <div className="right">
            <img src="assets/bg-1.jpg" />
          </div>
          <a href="#"><img src="assets/right-arrow-bold (copy).svg" className="right-arrow"></img></a>
        </div>
      </div>

      {/* Explore Section */}
      <div className="explore wrapper">
        <div className="container">
          <ul>
            <li>
              <h1>Low Price</h1>
              <h2>High Coziness</h2>
              <p>UPTO 50% OFF</p>
              <Link to="/explore">Explore Items</Link>
            </li>
            <li className="Beyoung">
              <h1>Beyoung Presents</h1>
              <h2>Breezy Summer <br /> Style</h2>
              <p>UPTO 50% OFF</p>
              <Link to="/explore">Explore Items</Link>
            </li>
          </ul>
        </div>
      </div>



      {/* New Arrival Section */}
      <div className="new-arrival wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" />
          <h1>New Arrival</h1>
        </div>

        <div className="slider-container">
          <button className="slide-btn left-btn" onClick={handleClickLeft} disabled={currentSlide === 0}>
            <img src="assets/arrow-left.svg" alt="left arrow" />
          </button>

          <div className="bottom">
            <div className="container">
              <ul
                className="image-list"
                style={{
                  display: "flex",
                  gap: "10px",
                  transform: `translateX(-${currentSlide * (100 / totalVisible)}%)`,
                  transition: "transform 0.5s ease-in-out",
                }}
              >
                {imageData.map((item, index) => (
                  <li key={index} className="slide-item">
                    <img src={item.src} alt={item.alt} className="new-arrival-img" />
                    <p>{item.label}</p>
                  </li>
                ))}
              </ul>
            </div>
          </div>

          <button className="slide-btn right-btn" onClick={handleClickRight} disabled={currentSlide >= maxSlides}>
            <img src="assets/arrow-right.svg" alt="right arrow" />
          </button>
        </div>
      </div>

      {/* Shop Now Section */}
      <div className="shop-now wrapper">
        <div className="container">
          <div className="left">
            <h2>WE MADE YOUR EVERYDAY <br /> FASHION BETTER!</h2>
            <p>
              In our journey to improve everyday fashion, <br />
              euphoria presents EVERYDAY wear range - <br />
              Comfortable & Affordable fashion 24/7
            </p>
            <button className="shop-btn">Shop Now</button>
          </div>
          <div className="right">
            <img src="assets/friends.png" alt="friends" />
          </div>
        </div>
      </div>


      {/* Categories for Men Section */}
      <div className="Categories-men wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" />
          <h1>Categories For Men</h1>
        </div>
        <div className="bottom">
          <div className="container">
            {CategoriesData.map((item) => (
              <Link to={`/product/${item.id}`} key={item.id} className="category-card">
                <img src={item.image} alt={item.name} className="primary-img" />
                <div className="category-info">
                  <div className="info">
                    <h2>{item.name}</h2>
                    <p>{item.description}</p>
                  </div>
                  <div className="rightshop-btn">
                    <img src="assets/arrow-right.svg" alt="RightArrow" />
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>

      {/* Categories for Women Section */}
      <div className="Categories-women wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" />
          <h1>Categories For Women</h1>
        </div>
        <div className="bottom">
          <div className="container">
            {CategoriesWomenData.map((item) => (
              <Link to={`/product/${item.id}`} key={item.id} className="category-card">
                <img src={item.image} alt={item.name} className="primary-img" />
                <div className="category-info">
                  <div className="info">
                    <h2>{item.name}</h2>
                    <p>{item.description}</p>
                  </div>
                  <div className="rightshop-btn">
                    <img src="assets/arrow-right.svg" alt="RightArrow" />
                  </div>
                </div>
              </Link>
            ))}
          </div>
        </div>
      </div>



      {/* Top-Brands Section */}
      <div className="top-brands wrapper">
        <div className="container">
          <img src="assets/Top Brands Deal.png" alt="top-brands" />
        </div>
      </div>

      {/* Limelight Section */}
      <div className="limelight wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" />
          <h2>In The Limelight</h2>
        </div>
        <div className="bottom">
          <div className="limelight-container">
            {Array.isArray(CategoriesLimelight) && CategoriesLimelight.length > 0 ? (
              CategoriesLimelight.map((item) => (
                <Link to={`/product/${item.id}`} key={item.id} className="product-card">
                  <img src={item.image} alt={item.name} className="primary-img" />
                  <img src="assets/heart.png" className="heart" alt="Favorite" />
                  <div className="limelight-info">
                    <div className="about">
                      <h3>{item.name}</h3>
                      <p>{item.brand}</p>
                    </div>
                    <div className="price">
                      <span className="price">{item.price}</span>
                    </div>
                  </div>
                </Link>
              ))
            ) : (
              <p>No products available</p>
            )}
          </div>
        </div>
      </div>



      {/* Feedback Section */}
      <div className="feedback wrapper">
        <div className="top">
          <img src="assets/Rectangle 21.png" alt="new arrival" /> 
          <h2>Feedback</h2>
        </div>

        <div className="bottom">

          <div className="feedback-container">
            {FeedbackData.map((feedback) => (
              <div key={feedback.id} className="feedback-card">
                <img src={feedback.image} alt={feedback.name} className="primary-img" />
                <h3>{feedback.name}</h3>


                {Array.isArray(feedback.comment) ? (
                  feedback.comment.map((line, index) => (
                        <p key={index}>{line}</p>
                  ))
                ) : (
                  <p>{feedback.comment}</p>
                )}

                <img src={feedback.ratingImage} alt="rating" className="rating-img" />
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default Main;
