import React, { useState, useEffect } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Navbar.css';

const Navbar = () => {
  const [menuOpen, setMenuOpen] = useState(false);
  const [activeItem, setActiveItem] = useState("Shop");
  const [showSearch, setShowSearch] = useState(false);
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 640);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 640);
      if (window.innerWidth > 640) {
        setShowSearch(true); // always show on larger screens
      } else {
        setShowSearch(false); // hidden initially on small screens
      }
    };

    handleResize(); // set initial
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="navbar wrapper">
      <div className="nav-logo">
        <img src="assets/Logo.svg" alt="logo" />
      </div>

      <div className="nav-middle">
        <i 
          className="fa-solid fa-bars menu-icon" 
          onClick={() => setMenuOpen(!menuOpen)}
        ></i>
        <ul className={`shop ${menuOpen ? 'open' : ''}`}>
          {["Shop", "Men", "Women", "Combos", "Joggers"].map((item) => (
            <li
              key={item}
              className={activeItem === item ? 'active' : ''}
              onClick={() => {
                setActiveItem(item);
                setMenuOpen(false);
              }}
            >
              {item}
            </li>
          ))}
        </ul>
      </div>

      <div className="nav-right">
        <div 
          className={`searchbar ${showSearch ? 'active' : ''}`} 
          onClick={() => {
            if (isMobile) setShowSearch(!showSearch);
          }}
        >
          <img src="assets/search.svg" alt="search" />
          <input 
            type="text" 
            placeholder="Search"
            style={{ display: !isMobile || showSearch ? 'block' : 'none' }}
          />
        </div>
      </div>

      <div className="nav-end">
        <div className="cart">
          <img src="assets/wishlist.svg" alt="wishlist" />
          <img src="assets/account.svg" alt="accounts" />
          <img src="assets/cart.svg" alt="cart" />
        </div>
      </div>
    </div>
  );
};

export default Navbar;
