import React from "react";
import { useParams, Link } from "react-router-dom";
import SingleProduct from "../datas/Single-product";
import "./Single-page.css";

export const SinglePage = () => {
    const { id } = useParams();
    const productId = parseInt(id, 10); 

    const product = SingleProduct.find((item) => item.id === productId);

    if (!product) {
        return <h2 style={{ textAlign: "center", marginTop: "60px" , marginBottom: " 100px", fontSize:"35px" , color:"#807d7e" , fontFamily: "poppins"}}>Product not found</h2>;
    }

    return (
        <div className="single-product-container">
          <div className="box">
            <div className="product-image">
              <img 
                  src={Array.isArray(product.images) ? product.images[0] : product.images} 
                  alt={product.name} 
                  className="product-image" 
              />
            </div>
            <div className="product-details">
                <nav className="breadcrumb">
                    <Link to="/shop">Shop</Link> &gt;
                    <Link to={`/shop/${product.category?.toLowerCase() || "all"}`}>
                      {product.category || "Unknown"}
                    </Link> 
                    &gt; <span>{product.subCategory || "Uncategorized"}</span>
                </nav>
                <h1>{product.name}</h1>
                <img src="assets/Frame 15.png" className="star"></img>
                <img src="assets/Frame 22.png" className="size"></img>
                <img src="assets/Frame 23.png" className="colour"></img>
                <div className="cart">
                  <button>Add to cart <img src="assets/cart-1.svg"></img></button>
                  <h1>$63.00</h1>
                </div>
                <div class="feature-container">
                    <div class="feature-column">
                        <div class="feature-item">
                            <img src="assets/Frame 24.png" alt="Secure Payment"/>
                            <span>Secure payment</span>
                        </div>
                        <div class="feature-item">
                            <img src="assets/Frame 25.png" alt="Free Shipping"/>
                            <span>Free shipping</span>
                        </div>
                    </div>

                    <div class="divider"></div>

                    <div class="feature-column">
                        <div class="feature-item">
                            <img src="assets/Frame 26.png" alt="Size & Fit"/>
                            <span>Size & Fit</span>
                        </div>
                        <div class="feature-item">
                            <img src="assets/Frame 27.png" alt="Free Shipping & Returns"/>
                            <span>Free Shipping & Returns</span>
                        </div>
                    </div>
                </div>
            </div>
          </div>
          <div cla></div>
          <p>{product.description}</p>
          <h3>Price: ${product.price}</h3>
          <button className="buy-btn">Buy Now</button>

        </div>
    );
};

export default SinglePage;
